mapper.update:
update user


        user_name = #{userName},


        user_image = #{userImage}


where openID = #{openID}

update goods
set status = 1
where openID = #{openID}
and goods_id = #{goodsID}
and status = 0;

update goods
set create_time = #{newTime}
where goods_id = #{goodsID}
and openID = #{openID}
and timestampdiff(hour, create_time, #{newTime}) >= 24

update goods

    set status = 2


    set status = 0

where openID = #{openID}
and goods_id = #{goodsID}

mapper.delete:
delete from goods
where goods_id = #{goodsID}
and openID = #{openID}

mapper.update:
